// ESERCIZIO 1
function checkAge() {
    const myAge = 32;
    let message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = `Hai più di 18 anni!`;
    }
}
checkAge();
console.log(checkAge());

